import React, {useState, useEffect} from 'react'
import axios from 'axios';
import Dropdown from 'react-bootstrap/Dropdown'
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {

  const [data,setData] = useState([{}])
  const [editedData,setEditedData] = useState([{}])
  const [color, setColor] = useState([{}])

  useEffect(() => {
    fetch("/members").then(
      res => res.json()
    ).then(
      data => {
        setData(data)
        console.log(data)
      }
    )
  },[editedData])


  useEffect(() => {
    switch(transform){
      case 'Mult':
        color => setColor('success')
      case 'Inv':
        color => setColor('danger')
    }

  },[transform])

  const editArr = (i) => {
    // Simple POST request with a JSON body using axios
    const article = { 'members': data.members};
    article.members[i] += 1
    axios.post('/members', article).then(response => article).then(
      editedData => {
        setEditedData(article)
        console.log(article)
      }
    );
  }

  const adjustTransform = (type) => {
    transform => {
      setTransform(type)
    }
  }

  return (
    <div>
      <div>
      <Dropdown>
        <Dropdown.Toggle variant={color} id="dropdown-basic">
          Dropdown Button
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item onClick = {adjustTransform('Gate')} >Gate</Dropdown.Item>
          <Dropdown.Item onClick = {adjustTransform('Inv')}>Invert</Dropdown.Item>
          <Dropdown.Item onClick = {adjustTransform('Mult')}>Multiplication</Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>
</div>
    <div>
      {(typeof data.members === 'undefined') ? (
        <p>Loading...</p>
      ) : (
        data.members.map((member, i ) => (

          <button key={i} onClick={() => editArr(i)}>{member}</button>

        ))
      )}
    </div>
    </div>
  )
}

export default App
