import React, {useState, useEffect} from 'react'
import axios from 'axios';
import Dropdown from 'react-bootstrap/Dropdown'
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {

  const [data,setData] = useState([{}])
  const [editedData,setEditedData] = useState([{}])
  const [transform, setTransform] = useState('')

  useEffect(() => {
    fetch("/members").then(
      res => res.json()
    ).then(
      data => {
        setData(data)
        console.log(data)
      }
    )
  },[editedData])

  const editArr = (i) => {
    // Simple POST request with a JSON body using axios
    const article = { 'members': data.members}
    article.members[i] = transform
    axios.post('/members', article).then(response => article).then(
      editedData => {
        setEditedData(article)
        console.log(article)
      }
    );
  }

  const adjustTransform = (type) => {
    setTransform(type)
  }

  return (
    <div>
      <style type="text/css">
    {`
    .btn-Gate {
      background-color: #42b5d4;
      margin-left: 10px;
      margin-top: 10px;
      font-size: 25px;
      justify-content: space-between; 
      min-height: 50px;
      min-width: 50px;
    }
    .btn-Inv {
      background-color: #b879ed;
      margin-left: 10px;
      margin-top: 10px;
      font-size: 25px;
      justify-content: space-between; 
      min-height: 50px;
      min-width: 50px;
    }
    .btn-Mult {
      background-color: #edb879;
      margin-left: 10px;
      margin-top: 10px;
      font-size: 25px;
      justify-content: space-between; 
      min-height: 50px;
      min-width: 50px;
    }
    .btn-None {
      background-color: grey;
      margin-left: 10px;
      margin-top: 10px;
      font-size: 25px;
      border: black;
      color: white;
      justify-content: space-between; 
      min-height: 50px;
      min-width: 50px;
    }
    .btn-Gen {
      background-color: #79aeed;
      margin-left: 10px;
      margin-top: 10px;
      font-size: 25px;
      color: white;
      border: black;
      justify-content: space-between; 
      min-height: 50px;
      min-width: 50px;
    }
    .btn-outline-secondary{
      margin-left: 10px;
      margin-top: 10px;

      min-height: 50px;
      min-width: 50px;
    }
    .dropdown-menu a{
      color: #42b5d4;
    }

    `}
  </style>

      <div>

      <Dropdown>
        <Dropdown.Toggle variant={transform==''?'None':transform} id="dropdown-basic">
          {transform==''?'None':transform}
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item className="Gate" onClick = {() => adjustTransform('Gate')}>Gate</Dropdown.Item>
          <Dropdown.Item onClick = {() => adjustTransform('Inv')}>Invert</Dropdown.Item>
          <Dropdown.Item onClick = {() => adjustTransform('Mult')}>Multiplication</Dropdown.Item>
          <Dropdown.Item onClick = {() => adjustTransform('')}>None</Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>

</div>
    <div>
      {(typeof data.members === 'undefined') ? (
        <p>Loading...</p>
      ) : (
        data.members.map((member, i ) => (

          <Button className="rounded-circle"  variant={member==''?"outline-secondary":member} key={i} onClick={() => editArr(i)}></Button>

        ))
      )}
    </div>

    <Button variant={'Gen'}>Generate</Button>
    </div>
  )
}

export default App
